SELECT * FROM pj_class_selection 

	WHERE 1=1
	
		AND id = #{args[0].id,javaType=java.lang.Integer}
	
	
		AND school_id = #{args[0].schoolId,javaType=java.lang.Integer}
	
	
		AND public_class_id = #{args[0].publicClassId,javaType=java.lang.Integer}
	
	
		AND team_id = #{args[0].teamId,javaType=java.lang.Integer}
	
	
		AND student_id = #{args[0].studentId,javaType=java.lang.Integer}
	
	
		
			
				AND is_delete  = 1
			
			
				AND is_delete  = 0
			
		
	
	
		AND create_date = #{args[0].createDate,javaType=java.util.Date}
	
	
		AND modify_date = #{args[0].modifyDate,javaType=java.util.Date}

