SELECT * FROM pj_school_attribute_platform WHERE 1=1

AND id = #{args[0],javaType=java.lang.Integer}
  
ORDER BY id DESC

SELECT * FROM pj_school_attribute_platform 

	WHERE 1=1
	
		AND id = #{args[0].id,javaType=java.lang.Integer}
	
	
		AND name = #{args[0].name,javaType=java.lang.String}
	
	
		AND memo = #{args[0].memo,javaType=java.lang.String}
	
	
		AND create_date = #{args[0].createDate,javaType=java.util.Date}
	
	
		AND modify_date = #{args[0].modifyDate,javaType=java.util.Date}
	
	
		
			
				AND is_deleted  = 1
			
			
				AND is_deleted  = 0

SELECT count(*) FROM pj_school_attribute_platform 

	WHERE 1=1
	
		AND id = #{args[0].id,javaType=java.lang.Integer}
	
	
		AND name = #{args[0].name,javaType=java.lang.String}
	
	
		AND memo = #{args[0].memo,javaType=java.lang.String}
	
	
		AND create_date = #{args[0].createDate,javaType=java.util.Date}
	
	
		AND modify_date = #{args[0].modifyDate,javaType=java.util.Date}
	
	
		
			
				AND is_deleted  = 1
			
			
				AND is_deleted  = 0
