	AND grade_number = #{args[0].gradeNumber,javaType=java.lang.Integer}
	
	
	AND days = #{args[0].days,javaType=java.lang.Integer}
	
	
	AND days_plan = #{args[0].daysPlan,javaType=java.lang.String}
	
	
