select sub.`name` as name, count(pst.subject_code) as teacherNumberOfSubject from pj_subject sub LEFT JOIN pj_subject_teacher pst on sub.`code` = pst.subject_code
AND pst.school_id = #{args[0]}
WHERE 1=1
AND sub.school_id = #{args[0]}
AND sub.is_delete = 0 GROUP BY sub.code

