SELECT tt.*, t.name As team_name, t.code2 AS team_code, g.name AS grade_name, g.uni_grade_code AS grade_code, GROUP_CONCAT(DISTINCT type) As types, GROUP_CONCAT(subject_code,',',subject_name SEPARATOR ';') As subject_info, GROUP_CONCAT(tt.modify_date SEPARATOR ';') AS modifyTimes FROM pj_team_teacher tt LEFT JOIN pj_team t on t.id = tt.team_id LEFT JOIN pj_grade g on g.id = t.grade_id WHERE tt.is_delete = 0 AND t.is_delete=0 AND g.is_deleted = 0
AND t.school_id = #{args[0].schoolId,javaType=java.lang.Integer}
AND tt.school_year = #{args[0].schoolYear,javaType=java.lang.String}
AND tt.teacher_id = #{args[0].teacherId,javaType=java.lang.Integer}
AND tt.user_id = #{args[0].userId,javaType=java.lang.Integer}
GROUP BY tt.team_id ORDER BY g.uni_grade_code, t.team_number

