SELECT ( CASE pq.question_type WHEN 'radio' THEN '单选题' WHEN 'checkbox' THEN '多选题' WHEN 'blank' THEN '填空题' WHEN 'word' THEN '简答题' WHEN 'complex' THEN '复合题' WHEN 'trueOrFalse' THEN '判断题' WHEN 'blank' THEN '填空题' ELSE '未知题型' END ) `question_type`, pq.`pos`, pq.`uuid`, pp.`id` as paper_id FROM `pa_paper` pp JOIN `pa_paper_question` ppq ON pp.`paper_uuid` = ppq.`paper_uuid` JOIN `pa_question` pq ON ppq.`question_uuid` = pq.`question_uuid` WHERE pp.`id` IN
#{item}
ORDER BY pq.`pos`
