SELECT ( CASE ppt.`question_type` WHEN 'radio' THEN '单选题' WHEN 'checkbox' THEN '多选题' WHEN 'blank' THEN '填空题' WHEN 'word' THEN '简答题' WHEN 'complex' THEN '复合题' WHEN 'trueOrFalse' THEN '判断题' WHEN 'blank' THEN '填空题' ELSE '未知题型' END ) `question_type`, pq.`uuid`, ppt.`pos`, pq.`correct_answer`, puq.`answer`, ppt.`score` AS correct_score, puq.`score`, puq.`user_id`, pq.`uuid` AS questionUuid, puq.`id` AS user_question_id, puq.uuid AS user_question_uuid, pq.question_property, pq.content FROM `pa_question` pq LEFT JOIN pa_paper_tree ppt ON pq.`id` = ppt.`question_id` LEFT JOIN `pa_user_question` puq ON puq.`question_uuid` = pq.`uuid` WHERE 1 = 1
AND puq.`owner_id`=#{args[0]}
AND puq.`object_id`=#{args[1]}
AND puq.`type`=2
AND puq.`user_id`=#{args[2]}
AND pq.`uuid`=#{args[3]}
group by puq.`id`

SELECT ( CASE ppt.`question_type` WHEN 'radio' THEN '单选题' WHEN 'checkbox' THEN '多选题' WHEN 'blank' THEN '填空题' WHEN 'word' THEN '简答题' WHEN 'complex' THEN '复合题' WHEN 'trueOrFalse' THEN '判断题' WHEN 'blank' THEN '填空题' ELSE '未知题型' END ) `question_type`, pq.`uuid`, ppt.`pos`, pq.`correct_answer`, puq.`answer`, ppt.`score` AS correct_score, puq.`score`, puq.`user_id`, pq.`uuid` AS questionUuid, puq.`id` AS user_question_id, puq.uuid AS user_question_uuid, pq.question_property, pq.content, puq.create_date AS createTime, puq.modify_date AS modifyTime FROM `pa_question` pq LEFT JOIN pa_paper_tree ppt ON pq.`id` = ppt.`question_id` LEFT JOIN `pa_user_question` puq ON puq.`question_uuid` = pq.`uuid` WHERE 1 = 1
AND puq.`owner_id`=#{args[0]}
AND puq.`object_id`=#{args[1]}
AND puq.`type`=2
AND puq.`user_id`=#{args[2]}
AND puq.`team_id`=#{args[3]}
AND pq.`uuid`=#{args[4]}
group by puq.`id`

