mapper.select:
SELECT * FROM pa_paper_question WHERE 1=1 AND paper = #{args[0].paperId,javaType=java.lang.String} AND
pos = #{pos}
AND question IS NULL ORDER BY pos

SELECT pq.* FROM pa_user_answer ua LEFT JOIN pa_paper_question pq WHERE 1=1 AND ua.correct = ${@platform.education.paper.constants.CorrectType@INCORRECT} AND ua.finalAnswer = ${@platform.education.paper.constants.AnswerConstants@FINAL_ANSWER} AND ua.user_id = #{args[0].userId,javaType=java.lang.String}
AND pq.subject_code = #{args[0].subjectCode,javaType=java.lang.String}
AND ua.paper_question = pq.uuid ORDER BY ua.create_time DESC

SELECT pq.* FROM pa_paper_question pq LEFT JOIN pa_collect pc WHERE 1=1 AND pq.id = pc.relate_id AND pc.user_id = #{args[0].userId,javaType=java.lang.String} AND pc.collect_type = #{args[0].collectType,javaType=java.lang.String}
AND pq.subject_code = #{args[0].subjectCode,javaType=java.lang.String}
ORDER BY pc.create_time DESC
