SELECT pp.*,ppc.stage_code FROM pa_paper pp
INNER JOIN pa_paper_catalog ppc
ON pp.id=ppc.paper_id
WHERE pp.is_deleted=0

	AND ppc.stage_code=#{args[1].stageCode}


	AND ppc.subject_code=#{args[1].subjectCode}


	
		AND pp.owner_mode=0
	
	
		AND pp.owner_mode=1
		AND pp.owner_uid=#{args[0].ownerUid}
	
	
		AND pp.owner_mode=2
		AND pp.user_id=#{args[0].userId}
	

GROUP BY pp.id

SELECT pp.*,ppc.stage_code FROM pa_paper pp INNER JOIN pa_paper_catalog ppc ON pp.id=ppc.paper_id WHERE pp.is_deleted=0
AND ppc.catalog_code
=#{args[1].code}
IN(SELECT code FROM jc_textbook_catalog WHERE test_book_id=#{args[1].textbookId})
IN(SELECT code FROM res_textbook_catalog WHERE test_book_id=#{args[1].textbookId})
AND pp.owner_mode=0
AND pp.owner_mode=1 AND pp.owner_uid=#{args[0].ownerUid}
AND pp.owner_mode=2 AND pp.user_id=#{args[0].userId}
GROUP BY pp.id

SELECT pp.*,ppc.stage_code FROM pa_paper pp
INNER JOIN pa_favorites pf
ON pp.id=pf.object_id
INNER JOIN pa_paper_catalog ppc
ON pp.id=ppc.paper_id
WHERE pp.is_deleted=0

	AND ppc.stage_code=#{args[0].stageCode}


	AND ppc.subject_code=#{args[0].subjectCode}

AND pf.poster_id=#{args[1]}
AND pf.object_type=#{args[2].objectType}
GROUP BY pp.id

