SELECT pt.* FROM pa_task pt
INNER JOIN pa_paper pp ON
pt.paper_id=pp.id
INNER JOIN pa_paper_catalog ppc ON pt.paper_id=ppc.paper_id
AND ppc.subject_code=#{args[0].subjectCode}
AND ppc.stage_code=#{args[0].stageCode}
WHERE
pt.publisher_id=#{args[1]}
AND pt.title like CONCAT(CONCAT('%', #{args[0].title,javaType=java.lang.String}),'%')
AND pt.is_deleted=0

	SELECT pt.* FROM pa_task_team ptt 

    INNER JOIN pa_task pt ON pt.id=ptt.task_id

    WHERE ptt.pj_exam_id=#{args[0]}

mapper.update:
UPDATE  pa_task_team  
SET pj_exam_id=#{args[1]}
where id = #{args[0]}

mapper.select:
SELECT pt.id, pt.publisher_name, pt.create_date, SUM( pt.user_count ) AS user_count, SUM( pt.finished_count ) AS finished_count, js.`name` AS subjectName, pp.title FROM pa_task pt INNER JOIN pa_paper pp ON pt.paper_id = pp.id INNER JOIN pa_task_team ptt ON pt.id = ptt.task_id INNER JOIN pa_paper_catalog ppc ON pt.paper_id = ppc.paper_id LEFT JOIN jc_subject js ON ppc.subject_code = js.`code` LEFT JOIN pj_team pjt ON ptt.team_id = pjt.id WHERE 1=1
AND pjt.id = #{args[0]}
AND ppc.subject_code = #{args[1]}
AND pt.title like CONCAT('%',#{args[2]},'%')
AND pjt.school_id = #{args[3]}
AND pjt.grade_id =#{args[4]}
AND pt.is_deleted = 0 AND ptt.is_deleted = 0 AND pp.is_deleted = 0 AND pjt.is_delete = 0 GROUP BY pt.paper_id ORDER BY pt.start_time DESC
